    /**
     * 下载网络图片
     * @param $url
     * @param $filestring
     * @return bool|string
     */
    private function downLoadPic($url){
        if(!$url) return false;
        $urlInfo = explode('/', $url);
        $filename = end($urlInfo);
        // 检查路径是否存在，如不存在则创建
        $dir = Yii::$app->getBasePath(true).'/web/images/hsz/';
        $imgUrl = Url::to('@web/images/hsz/'.$filename, true);
        if (!is_dir($dir)){
            //第三个参数是“true”表示能创建多级目录，iconv防止中文目录乱码
            $res=mkdir(iconv("UTF-8", "GBK", $dir),0777,true);
        }
        if(file_exists($dir.$filename)) return $imgUrl;
        ob_start();
        readfile($url);
        $img=ob_get_contents();
        ob_end_clean();
        $size=strlen($img);
        $fp2=fopen($dir.$filename,"a");
        if(fwrite($fp2,$img) === false){
            return 'dolwload image falied. Error Info: 无法写入图片'.date('Y-m-d H:i:s');
        }
        fclose($fp2);
        return $imgUrl;
    }

